public static ExternalMethodCallResult ValidateDelete(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	ExternalMethodCallResult res = new ExternalMethodCallResult(true);
	foreach (dynamic e in entities)
	{
		var state = e.State.ToLower();
		if(state != "draft" && state != "deleted")
		{
		    res.Sucess = false;
		    res.AddGlobalError(OptimaJet.Localization.LocalizationProvider.Provider.Get("You can delete documents in Draft status only!"));
		    break;
		}
	}

	return res;
}

public static ExternalMethodCallResult SetValueCommonSettings(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	foreach (var pair in parameters)
	{
	    if (metadata.GetAttributeByPropertyName(pair.Key) == null)
	    {
	        throw new Exception(string.Format("Attribute {0} not found.", pair.Key));
	    }
	}

	foreach (var pair in parameters)
	{
	    foreach (var e in entities)
	    {
	        if (CommonSettings.IsParam(pair.Value))
	        {
	            e[pair.Key] =
	                metadata.GetAttributeByPropertyName(pair.Key)
	                    .DescriptionType.ParseToCLRType(CommonSettings.GetParam(pair.Value));
	        }
	        else
	        {
	            e[pair.Key] =
	                metadata.GetAttributeByPropertyName(pair.Key)
	                    .DescriptionType.ParseToCLRType(pair.Value);
	        }
	    }

	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult SetMismatchFlag(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	if (entities.Count() > 0)
	{
		var idsElement = new XElement("ids");
		foreach (dynamic e in entities)
		{
		    idsElement.Add(new XElement("id") { Value = e.GetId().ToString("D") });
		}

		var executeMismathSp = string.Format("EXEC [dbo].[SetMismatchFlagToInvoicesByInvoiceId] '{0}'", idsElement);
		DynamicRepository.GetByQuery(executeMismathSp);
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult FormSettings(string formName, string id,
   Dictionary<string, string> request,
   ref VisibilitySettings visibility,
   ref Dictionary<string, string> formparameters)
{
	#region   
	if (!string.IsNullOrEmpty(id))
	{
	    Guid? guid = new Guid(id);             
	    if (!guid.HasValue)
	        return CommonMethods.AccessDeniedResult();

	    var rep = new DynamicEntityRepository();
	    var lMetadata = rep.GetEntityMetadataByEntityName("Invoice", 1);

	    if (!CommonMethods.VisibilityAccessCheck(lMetadata, guid.Value, GetVisibilityFilter(lMetadata)))
	    {
	        return CommonMethods.AccessDeniedResult();
	    }
	}          
	#endregion

	#region CheckIsDeleted

	var parameters = new Dictionary<string, string>();
	parameters.Add("MetaView", "Invoice");
	VisibilitySettings visibilityForCheckIsDeleted = new VisibilitySettings();
	Platform.ExternalMethods.Dictionaries.FormSettingsCheckGlobalReadOnly(formName, id, parameters, request, ref visibilityForCheckIsDeleted, ref formparameters);
	if (visibilityForCheckIsDeleted.GlobalReadOnly)
	{
	    visibility.GlobalReadOnly = true;
	    return new ExternalMethodCallResult(true);
	}
	#endregion

	var selectquerytypeKey =
	    request.Keys.FirstOrDefault(k => k.Equals("selectquerytype", StringComparison.InvariantCultureIgnoreCase));
	var isCopy = !(string.IsNullOrEmpty(selectquerytypeKey)) && int.Parse(request[selectquerytypeKey]) == (int)EntityOperationType.Copy;
	var trueId = isCopy ? null : id;


	if (!string.IsNullOrWhiteSpace(id) && !isCopy)
	{
	    var item = DynamicRepository.GetByEntity("Invoice",
	            FilterCriteriaSet.And.Equal(id, "Id")).FirstOrDefault();
	    if (item != null)
	    {
	        if (item.State != (byte)0)
	        {
	            visibility.GlobalReadOnly = true;
	        }
	    }
	}

	return ExternalMethodCallResult.SucessResult;
}

public static FilterCriteriaSet GetVisibilityFilter(EntityMetadata lMetadata)
{
    return FilterCriteriaSet.Empty;
}

public static ExternalMethodCallResult ValidateRecord(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	ExternalMethodCallResult res = new ExternalMethodCallResult(true);

	string optype = parameters["OPTYPE"].ToUpper();
	string[] columns = parameters["COLUMNS"].Split(',');
	CheckAttributeInMetadata(metadata, columns);

	foreach (var e in entities)
	{
	    FilterCriteriaSet filter = FilterCriteriaSet.Empty;

	    if (optype == "UNIQUE" || optype == "UNIQUE_BLOCK")
	    {
	        foreach (var columnName in columns)
	        {
	            var attribute = metadata.GetAttributeByPropertyName(columnName);
	            var column = metadata.GetColumn(attribute);

	            var typeFilter =
	                optype == "UNIQUE" ?
	                FilterCriteriaSet.Or.Equal(attribute.DescriptionType.ParseToCLRType(e[columnName]), column) :
	                FilterCriteriaSet.And.Equal(attribute.DescriptionType.ParseToCLRType(e[columnName]), column);

	            filter = filter == FilterCriteriaSet.Empty ? typeFilter : filter.Merge(typeFilter);                        
	        }

	        var attributeId = metadata.GetAttributeByPropertyName(metadata.PrimaryKeyPropertyName);
	        var columnId = metadata.GetColumn(attributeId);
	        var idFilter = FilterCriteriaSet.And.NotEqual(attributeId.DescriptionType.ParseToCLRType(e[metadata.PrimaryKeyPropertyName]), columnId);
	        filter = filter == FilterCriteriaSet.Empty ? idFilter : FilterCriteriaSet.And.Merge(filter).Merge(idFilter);

	        var globalfilter = DynamicRepository.GetFilterExternalMethods(metadata.Name, metadata);
	        if (globalfilter != null && globalfilter != FilterCriteriaSet.Empty)
	            filter = filter == FilterCriteriaSet.Empty ? globalfilter : FilterCriteriaSet.And.Merge(filter).Merge(globalfilter);
	    }
	    else
	    {
	        throw new Exception(string.Format("OPTYPE={0} is not supported", optype));
	    }

	    var rep = DynamicRepository.GetByView(metadata.Name, filter);
	    if (rep.Count > 0)
	    {
	        if (parameters.ContainsKey("ERRORMSG"))
	        {
	            res.AddGlobalError(parameters["ERRORMSG"]);
	        }
	        else if (optype == "UNIQUE")
	        {
	            foreach (var columnName in columns)
	            {
	                foreach (var existItem in rep)
	                {
	                    if (e[columnName].Equals(existItem[columnName]))
	                    {
	                        var attribute = metadata.GetAttributeByPropertyName(columnName);
	                        res.AddGlobalError(string.Format(OptimaJet.Localization.LocalizationProvider.Provider.Get("{0} field must be unique"),
	                            string.Format("{0} ({1})", attribute.Caption, e[columnName])));
	                        res.Sucess = false;
	                        return res;
	                    }
	                }
	            }
	        }
	        else
	        {
	            res.AddGlobalError(OptimaJet.Localization.LocalizationProvider.Provider.Get("RecordIsNotValid"));
	        }

	        res.Sucess = false;                    
	        break;
	    }
	}

	return res;
}

private static void CheckAttributeInMetadata(EntityMetadata metadata, string[] columns)
{
    foreach (var col in columns)
    {
        if (metadata.GetAttributeByPropertyName(col) == null)
        {
            throw new Exception(string.Format("Attribute {0} not found.", col));
        }
    }
}

public static ExternalMethodCallResult ValidateDelete(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	ExternalMethodCallResult res = new ExternalMethodCallResult(true);
	foreach (dynamic e in entities)
	{
		var state = e.State;
		if (state != (byte)InvoiceStatus.Draft)
		{
		    res.Sucess = false;
		    res.AddGlobalError(OptimaJet.Localization.LocalizationProvider.Provider.Get("You can delete documents in Draft status only!"));
		    break;
		}
	}

	return res;
}

public static ExternalMethodCallResult InitItem(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	foreach (dynamic e in entities)
	{
		var sighters = DynamicRepository.GetByView("EntityRouteItemSighters", 
		    FilterCriteriaSet.And.Equal((Guid)e.Id, "EntityRouteItemId"), 
		    OrderByCriteriaSet.Asc("SecurityUserId_Name"));

		var sightersStr = new StringBuilder();
		foreach(var s in sighters)
		{
		    if(sightersStr.Length > 0)
		        sightersStr.Append(", ");
		    sightersStr.Append(s.SecurityUserId_Name);
		}

		e.Sighters = sightersStr.ToString();
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult FormCheckReadOnly(string formName, string id,
   Dictionary<string, string> parameters,
   Dictionary<string, string> request,
   ref VisibilitySettings visibility,
   ref Dictionary<string, string> formparameters)
{
	if (!parameters.ContainsKey("MetaView"))
	{
	    throw new Exception("Parameter MetaView not found!");
	}

	if (!SecurityCache.ViewCan(parameters["MetaView"], SecurityPermissionBaseTypeEnum.Edit))
	{
	    visibility.GlobalReadOnly = true;
	}
	else
	{
	    return Platform.ExternalMethods.Dictionaries.FormSettingsCheckIsDeleted(formName, id,parameters,request,ref visibility,ref parameters);
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult AddCommonSettingsFilter(EntityMetadata metadata, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	foreach (var pair in parameters)
	{
	    var attribute = metadata.GetAttributeByPropertyName(pair.Key);
	    if (attribute == null)
	    {
	        throw new Exception(string.Format("Attribute {0} not found.", pair.Key));
	    }

	    var column = metadata.GetColumn(attribute);
	    var typeFilter = FilterCriteriaSet.And.Equal(
	            attribute.DescriptionType.ParseToCLRType(CommonSettings.GetParam(pair.Value)), column);
	    filter = filter == FilterCriteriaSet.Empty ? typeFilter : FilterCriteriaSet.And.Merge(filter).Merge(typeFilter);
	}
	return ExternalMethodCallResult.SucessResult;
}

