public static ExternalMethodCallResult AddEasyFilter(EntityMetadata metadata, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	foreach (var pair in parameters)
	{
	    FilterCriteriaSet itemFilter;
	    var attribute = metadata.GetAttributeByPropertyName(pair.Key);
	    if (attribute == null)
	    {
	        if (pair.Key == "FILTER")
	        {
	            itemFilter = FilterCriteriaSet.And.Custom(pair.Value);
	        }
	        else
	        {
	            throw new Exception(string.Format("Attribute {0} not found.", pair.Key));
	        }
	    }
	    else
	    {
	        var column = metadata.GetColumn(attribute);
	        itemFilter = FilterCriteriaSet.And.Equal(attribute.DescriptionType.ParseToCLRType(pair.Value), column);
	    }

	    filter = filter == FilterCriteriaSet.Empty ? itemFilter : FilterCriteriaSet.And.Merge(filter).Merge(itemFilter); 
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult SaveWorkflow(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	foreach (dynamic e in entities)
	{
		if (e.IsUseDesigner)
		{
		    var schemeCode = string.Format("Route_{0}", e.Id.ToString("N"));
		    var form = new System.Collections.Specialized.NameValueCollection();
		    form.Add("operation", "save");
		    form.Add("schemecode", schemeCode);
		    form.Add("data", e.workflowscheme);
		    Workflow.Runtime.DesignerAPI(form);
		}
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult StateFilter(EntityMetadata metadata, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	Dictionary<string, string> dictionary = new Dictionary<string, string>();
	if (!string.IsNullOrEmpty(extra))
	{
		dictionary = ServiceStack.Text.JsonSerializer.DeserializeFromString<Dictionary<string, string>>(HttpUtility.UrlDecode(extra));
	}

	var newFilter = FilterCriteriaSet.Empty;            
	if (dictionary.ContainsKey("statefilter") && dictionary["statefilter"].ToLower() == "1")
	{
		newFilter = FilterCriteriaSet.Or.Equal("Approved", "State").Equal("Final regional approval", "State");
	}
	else
	{
		newFilter = FilterCriteriaSet.And.Equal("Approved", "State");
	}

	filter = filter.IsEmpty ? newFilter : FilterCriteriaSet.And.Merge(filter).Merge(newFilter);
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult InitSubjects(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	DynamicEntityRepository repo = new DynamicEntityRepository();
	EntityMetadata employeeMetadata = repo.GetEntityMetadataByViewName("Employee", VisibilitySettings.Empty);
	EntityMetadata orgStructuresMetadata = repo.GetEntityMetadataByViewName("OrganizationalStructure", VisibilitySettings.Empty);
	string entitiesStr;
	string orgStructuresStr;

	foreach (dynamic e in entities)
	{

	    var accessEmployees = DynamicRepository.GetByView("EntityAccessEmployee",
	        FilterCriteriaSet.And.Equal((Guid)e.Id, "EntityAccessId"));

	    var accessOrgStructures = DynamicRepository.GetByView("EntityAccessOrganizationalStructure",
	        FilterCriteriaSet.And.Equal((Guid)e.Id, "EntityAccessId"));

	    entitiesStr = accessEmployees != null && accessEmployees.Any() ?
	        String.Format("<b>{0}</b>: {1}", employeeMetadata.Caption, 
	            String.Join(",", accessEmployees.Select(ae => ae.EmployeeId_SecurityUserId_Name)))
	        : entitiesStr = String.Empty;

	    orgStructuresStr = accessOrgStructures != null && accessOrgStructures.Any() ?
	        String.Format("<b>{0}</b>: {1}", orgStructuresMetadata.Caption, 
	            String.Join(",", accessOrgStructures.Select(aos => String.Format("{0} - {1}", aos.OrganizationalStructureId_ITArea, aos.OrganizationalStructureId_ITFunction))))
	        : String.Empty;

	    e.Subjects = entitiesStr + (!String.IsNullOrEmpty(entitiesStr) ? "<br>" : "") + orgStructuresStr;
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult DictionaryFilter(EntityMetadata metadata, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	if (!OptimaJet.Security.Providers.SecurityCache.CurrentUser.Roles.Contains("Admin"))
	{
		var enableOrgFilter = HttpContext.Current.Request.Params["EnableOrgFilter"];
		if (!string.IsNullOrEmpty(enableOrgFilter) && bool.Parse(enableOrgFilter))
		{
		    string filterStr = @"Id in (
		SELECT Id FROM Project freeProject WHERE NOT EXISTS(SELECT * FROM [dbo].[OrganizationalStructureProject] freeOrfProject WHERE freeOrfProject.ProjectId = freeProject.Id)

		UNION ALL

		SELECT ProjectId FROM [dbo].[OrganizationalStructureProject]
		where OrganizationalStructureId in 
		(select ParentId from [dbo].[vOrganizationalStructureParentsAndThis] where id = @OrgValue)) OR
		NOT EXISTS (
		SELECT ProjectId FROM [dbo].[OrganizationalStructureProject]
		where OrganizationalStructureId in 
		(select ParentId from [dbo].[vOrganizationalStructureParentsAndThis] where id = @OrgValue))";

		    var param = new Dictionary<string, object>();
		    param.Add("OrgValue", CommonSettings.CurrentDivision.Id);

		    var levelFilter = FilterCriteriaSet.And.Custom(filterStr, param);

		    filter = filter == FilterCriteriaSet.Empty ? levelFilter : FilterCriteriaSet.And.Merge(filter).Merge(levelFilter);
		}
	}
	return ExternalMethodCallResult.SucessResult;
}

public const string ViewAllPermission = "ViewAll";
public const string ViewAllParentPermission = "ViewAllParent";

public static ExternalMethodCallResult AddPermissionFilter(EntityMetadata metadata, string extra, ref FilterCriteriaSet filter)
{
    Dictionary<string, string> dictionary = new Dictionary<string, string>();
    if (!string.IsNullOrEmpty(extra))
    {
        dictionary = ServiceStack.Text.JsonSerializer.DeserializeFromString<Dictionary<string, string>>(HttpUtility.UrlDecode(extra));
    }

    var newFilter = FilterCriteriaSet.Empty;

    if (!dictionary.ContainsKey("type"))
    {
        if (SecurityCache.FormCan(metadata.TableName, ViewAllPermission))
        {
            newFilter = GetViewAll(metadata);
        }
        else if (SecurityCache.FormCan(metadata.TableName, ViewAllParentPermission))
        {
            newFilter = GetViewAllParent(metadata);
        }
        else
        {
            newFilter = GetView(metadata);
        }
    }

    filter = filter.IsEmpty ? newFilter : FilterCriteriaSet.And.Merge(filter).Merge(newFilter);

    return new ExternalMethodCallResult(true);
}

public static FilterCriteriaSet GetViewAllParent(EntityMetadata metadata)
{
    string queryWhere = String.Format("[{0}].[{1}].[CreatorEmployeeId] IN (SELECT e.[Id] \n"
       + "							FROM   [{0}].[Employee] e \n"
       + "									INNER JOIN ( \n"
       + "											SELECT [Id] \n"
       + "											FROM   [{0}].[GetParentOrganizationalStructure_fn]('{2}') \n"
       + "											UNION \n"
       + "											SELECT [Id] \n"
       + "											FROM   [{0}].[GetChildOrganizationalStructure_fn]('{2}') \n"
       + "										) t \n"
       + "										ON  t.[Id] = e.[OrganizationalStructureId])",
       metadata.SchemaName, metadata.TableName, (Guid)CommonSettings.CurrentDivision.Id);

    return FilterCriteriaSet.And.Custom(queryWhere);
}

public static FilterCriteriaSet GetView(EntityMetadata metadata)
{
    string queryWhere = String.Format("[{0}].[{1}].[CreatorEmployeeId] IN (SELECT e.[Id] \n"
      + "							FROM   [{0}].[Employee] e \n"
      + "									INNER JOIN ( \n"
      + "											SELECT [Id] \n"
      + "											FROM   [{0}].[GetChildOrganizationalStructure_fn]('{2}') \n"
      + "										) t \n"
      + "										ON  t.[Id] = e.[OrganizationalStructureId])",
      metadata.SchemaName, metadata.TableName, (Guid)CommonSettings.CurrentDivision.Id);

    return FilterCriteriaSet.And.Custom(queryWhere);
}

public static FilterCriteriaSet GetViewAll(EntityMetadata metadata)
{
    //         
    return FilterCriteriaSet.And.NotEqual(Null.Value, metadata.GetColumn("CreatorEmployeeId"));
}

