public static ExternalMethodCallResult AddVisibilityFilter(EntityMetadata metadata, EntityOperationType operationType, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	if (operationType == EntityOperationType.ServerCall)
	    return new ExternalMethodCallResult(true);

	var dictionary = new Dictionary<string, string>();

	if (!string.IsNullOrEmpty(extra))
	{
	    dictionary = ServiceStack.Text.JsonSerializer.DeserializeFromString<Dictionary<string, string>>(HttpUtility.UrlDecode(extra));
	}

	var newFilter = FilterCriteriaSet.Empty;

	if (dictionary.ContainsKey("type"))
	{
	    if (dictionary["type"].Equals("processed", StringComparison.InvariantCultureIgnoreCase))
	    {
	        newFilter = CommonMethods.GetProcessedFilter(metadata);
	    }
	    else if (dictionary["type"].Equals("inbox", StringComparison.InvariantCultureIgnoreCase))
	    {
	        newFilter = CommonMethods.GetInboxFilter(metadata);

	        if (metadata.TableName.Equals("BudgetItem", StringComparison.InvariantCultureIgnoreCase))
	        {
	            newFilter = FilterCriteriaSet.Or
	                .Merge(newFilter)
	                .Merge(BudgetItemGetMyDraftFilter(metadata));
	        }
	    }
	    else if (dictionary["type"].Equals("my", StringComparison.InvariantCultureIgnoreCase))
	    {
	        if (metadata.TableName.Equals("BudgetItem",StringComparison.InvariantCultureIgnoreCase))
	        {
	            newFilter = BudgetItemGetMyFilter(metadata);
	        }
	    }
	}

	filter = filter.IsEmpty ? newFilter : FilterCriteriaSet.And.Merge(filter).Merge(newFilter);
	return ExternalMethodCallResult.SucessResult;
}

public static FilterCriteriaSet BudgetItemGetMyFilter(EntityMetadata metadata)
{            
    return FilterCriteriaSet.And.Equal((Guid)CommonSettings.CurrentEmployee.Id, "CreatorEmployeeId").Prepare(metadata);
}

public static FilterCriteriaSet BudgetItemGetMyDraftFilter(EntityMetadata metadata)
{
    return BudgetItemGetMyFilter(metadata).Merge(FilterCriteriaSet.And.Custom("EntityRouteId is null").Prepare(metadata));
}

