public static ExternalMethodCallResult SetValueRecord(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	foreach (var pair in parameters)
	{
	    if(metadata.GetAttributeByPropertyName(pair.Key) == null)
	    {
	        throw new Exception(string.Format("Attribute {0} not found.", pair.Key));
	    }
	}

	foreach (var pair in parameters)
	{
	    foreach (var e in entities)
	    {
	        if (string.IsNullOrEmpty(pair.Value))
	            e[pair.Key] = null;
	        else
	            e[pair.Key] = metadata.GetAttributeByPropertyName(pair.Key).DescriptionType.ParseToCLRType(pair.Value);
	    }
	}
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult FormSettings(string formName,
            string id, Dictionary<string, string> request, ref VisibilitySettings visibility,
            ref Dictionary<string, string> formparameters)
        {
            #region   
            if (!string.IsNullOrEmpty(id))
            {
                Guid? guid = new Guid(id);
                if (!guid.HasValue)
                    return CommonMethods.AccessDeniedResult();

                var rep = new DynamicEntityRepository();
                var lMetadata = rep.GetEntityMetadataByEntityName("AllocationModel", 1);

                if (!CommonMethods.VisibilityAccessCheck(lMetadata, guid.Value, FilterCriteriaSet.Empty))
                {
                    return CommonMethods.AccessDeniedResult();
                }
            }
            #endregion

            #region CheckIsDeleted

            var parameters = new Dictionary<string, string>();
            parameters.Add("MetaView", "AllocationModel");
            VisibilitySettings visibilityForCheckIsDeleted = new VisibilitySettings();
            Platform.ExternalMethods.Dictionaries.FormSettingsCheckGlobalReadOnly(formName, id, parameters, request, ref visibilityForCheckIsDeleted, ref formparameters);
            if (visibilityForCheckIsDeleted.GlobalReadOnly)
            {
                visibility.GlobalReadOnly = true;
                return new ExternalMethodCallResult(true);
            }
            #endregion

            if (!string.IsNullOrWhiteSpace(id))
            {
                var item = DynamicRepository.GetByEntity("AllocationModel",
                        FilterCriteriaSet.And.Equal(id, "Id")).FirstOrDefault();
                if (item != null)
                {
                    if (SecurityCache.ViewCan("AllocationModel", SecurityPermissionBaseTypeEnum.Edit) 
                        && (item.EmployeeId == CommonSettings.CurrentEmployee.Id)
                        && !SecurityCache.ViewCan("AllocationModel", "EditAll"))
                    {
                        visibility.Add("AllocationModel_Edit", new VisibilitySetting() { ROP = new List<string>() { "EmployeeId" } });
                        visibility.Add("AllocationModelItem", new VisibilitySetting() { RO = false, EP = new List<string>() { "LegalEntityId", "CostCenterId", "Weight" } });
                    }
                    else if (!SecurityCache.ViewCan("AllocationModel", "EditAll"))
                    {
                        visibility.GlobalReadOnly = true;
                    }
                }
            }

            return ExternalMethodCallResult.SucessResult;
        }

public static ExternalMethodCallResult AddContractFilters(EntityMetadata metadata, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	var dictionary = new Dictionary<string, string>();
	if (!string.IsNullOrEmpty(extra))
	dictionary = ServiceStack.Text.JsonSerializer.DeserializeFromString<Dictionary<string, string>>(HttpUtility.UrlDecode(extra));

	if (!dictionary.ContainsKey("numberid"))
	return new ExternalMethodCallResult(true);

	var column = metadata.GetColumn("numberid");
	var idFilter = FilterCriteriaSet.And.NotEqual(dictionary["numberid"], column);
	filter = filter.IsEmpty ? idFilter : FilterCriteriaSet.And.Merge(filter).Merge(idFilter);
	return ExternalMethodCallResult.SucessResult;
}

public static ExternalMethodCallResult Calc(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	List<dynamic> fxratesplan = new List<dynamic>();

	List<Guid> budgetVersionIds = entities.Select(c => (Guid)c["BudgetVersionId"]).Distinct().ToList();
	if (budgetVersionIds.Count != 0)
	{
	    var budgetIds = DynamicRepository.GetByEntity("BudgetVersion",
	        FilterCriteriaSet.And.In(budgetVersionIds, "Id")).Select(c => (Guid)c["BudgetId"]).ToList();
	    if (budgetIds.Count > 0)
	    {
	        fxratesplan = DynamicRepository.GetByEntity("FXRatePlan", FilterCriteriaSet.And.In(budgetIds, "BudgetId"));
	    }
	}

	foreach (dynamic e in entities)
	{
	    float rate = fxratesplan.Where(c => (Guid)c.CurrencyId == e.CurrencyId).Select(c => (float)c.Rate).FirstOrDefault();
	    e.AmountUSD = Math.Round((float)e.AmountWithoutVAT * rate, 2);
	    e.AmountWithVATUSD = Math.Round((float)e.AmountUSD * ( 1 + (float)e.VAT / 100), 2);
	}
	return ExternalMethodCallResult.SucessResult;
}

