public static ExternalMethodCallResult InitItem(EntityMetadata metadata,
   Dictionary<string, string> parameters,
   IEnumerable<DynamicEntity> entities)
{
	foreach (dynamic e in entities)
	{
	    var accessEntities = DynamicRepository.GetByView("EntityAccessItemEntity",
	        FilterCriteriaSet.And.Equal((Guid)e.Id, "EntityAccessItemId"),
	        OrderByCriteriaSet.Asc("AccessEntityCaption"));

	    var entitiesStr = new StringBuilder();
	    foreach (var s in accessEntities)
	    {
	        if (entitiesStr.Length > 0)
	            entitiesStr.Append(", ");
	        entitiesStr.Append(s.AccessEntityCaption);
	    }

	    e.AccessEntities = entitiesStr.ToString();
	}
	return ExternalMethodCallResult.SucessResult;
}

