}

public static ExternalMethodCallResult FormSettings(string formName, string id,
	Dictionary<string, string> request,
	ref VisibilitySettings visibility,
	ref Dictionary<string, string> formparameters)
{	
    #region Check access
    if (!string.IsNullOrEmpty(id))
    {
        Guid? guid = new Guid(id);
        if (!guid.HasValue)
            return CommonMethods.AccessDeniedResult();

        var rep = new DynamicEntityRepository();
        var lMetadata = rep.GetEntityMetadataByEntityName("BudgetItem", 1);

        if (!CommonMethods.VisibilityAccessCheck(lMetadata, guid.Value, GetVisibilityFilter(lMetadata)))
        {
            return CommonMethods.AccessDeniedResult();
        }
    }
    #endregion

    visibility.Add("WorkflowHistory", new VisibilitySetting() { RO = true });
    visibility.Add("Invoice", new VisibilitySetting() { RO = true });

    var routes = WorkflowOperations.GetFormattedRoutesForUser(CommonSettings.CurrentEmployee.SecurityUserId);
    formparameters.Add("Routes", routes);

    #region CheckIsDeleted

    var parameters = new Dictionary<string, string>();
    parameters.Add("MetaView", "BudgetItem");
    VisibilitySettings visibilityForCheckIsDeleted = new VisibilitySettings();
    Platform.ExternalMethods.Dictionaries.FormSettingsCheckGlobalReadOnly(formName, id, parameters, request, ref visibilityForCheckIsDeleted, ref formparameters);
    if (visibilityForCheckIsDeleted.GlobalReadOnly)
    {
        visibility.GlobalReadOnly = true;
        formparameters.Add("Commands", "[]");
        formparameters.Add("States", "[]");
        return new ExternalMethodCallResult(true);
    }
    #endregion

    var selectquerytypeKey =
        request.Keys.FirstOrDefault(k => k.Equals("selectquerytype", StringComparison.InvariantCultureIgnoreCase));
    var isCopy = !(string.IsNullOrEmpty(selectquerytypeKey)) && int.Parse(request[selectquerytypeKey]) == (int)EntityOperationType.Copy;
    var trueId = isCopy ? null : id;

    Guid? entityRouteId = null;
    dynamic item = null;
    if (!string.IsNullOrEmpty(id))
    {
        item = DynamicRepository.GetByEntity("BudgetItem", FilterCriteriaSet.And.Equal(new Guid(id), "Id")).FirstOrDefault();
        if (item != null)
        {
            if (item.IsPrevPeriod)
            {
                formparameters.Add("Commands", "[]");
                formparameters.Add("States", "[]");
                if (!SecurityCache.FormCan("BudgetItem", "EditAutoCreatedItems"))
                    visibility.GlobalReadOnly = true;

                return new ExternalMethodCallResult(true);
            }

            entityRouteId = item.EntityRouteId;
        }
    }

    var isProcessExist = !string.IsNullOrEmpty(trueId) && OptimaJet.BJet.VTB.Workflow.Runtime.IsProcessExists(new Guid(trueId)) ? "1" : "0";
    formparameters.Add("IsProcessExist", isProcessExist);

    var commands = new List<WorkflowCommand>();
    if (entityRouteId.HasValue)
    {
        formparameters.Add("RouteId", entityRouteId.Value.ToString("N"));
        commands = WorkflowOperations.GetFormattedCommands(trueId, entityRouteId);
    }
    else if (isCopy || (item == null || item.CreatorEmployeeId == CommonSettings.CurrentEmployee.Id))
    {
        List<dynamic> routesForUser = WorkflowOperations.GetRoutesForUser(CommonSettings.CurrentEmployee.SecurityUserId);
        if (routesForUser.Count > 0)
        {
            Guid? route = (Guid?)routesForUser[0].Id;
            commands = WorkflowOperations.GetFormattedCommands(string.Empty, route);
        }
    }

    formparameters.Add("Commands", WorkflowOperations.ConvertWorkflowCommandsToJSON(commands));

    var states = SecurityCache.CheckPermission("Form_BudgetItem", "SetState") ?
            WorkflowOperations.GetFormattedStates(trueId) : "[]";
    formparameters.Add("States", states);

    if (!string.IsNullOrWhiteSpace(trueId))
    {
        var prevcomment = WorkflowOperations.GetPrevComment(trueId);
	formparameters.Add("WFPrevComment", prevcomment == null ? HttpUtility.UrlEncode(String.Empty) : HttpUtility.UrlEncode(prevcomment).Replace("+", "%20"));
    }

    SetVisibility(trueId, item, visibility, commands);
    return ExternalMethodCallResult.SucessResult;
}

private static void SetVisibility(string trueId, dynamic budgetItem, VisibilitySettings visibility, List<WorkflowCommand> commands)
{   
    if (string.IsNullOrEmpty(trueId) || budgetItem == null)
        return;

    if (budgetItem.EntityRouteId == null)
    {
        //
        var creatorEmployee = DynamicRepository.GetByEntity("Employee",
            FilterCriteriaSet.And.Equal((Guid)budgetItem.CreatorEmployeeId, "Id")).FirstOrDefault();

        if (creatorEmployee != null && CommonSettings.CurrentEmployeeChildrenOrganizationalStructures.Any(i => i == creatorEmployee.OrganizationalStructureId))
            return;
        visibility.GlobalReadOnly = true;
        return;
    }

    if ((budgetItem.AllowEdit || budgetItem.WasBack) && commands.Any())
        return;

    visibility.GlobalReadOnly = true;
}

public static FilterCriteriaSet GetVisibilityFilter(EntityMetadata lMetadata)
{
    return FilterCriteriaSet.Empty;
}

