public static ExternalMethodCallResult InvoiceFilterForBudgetItem(EntityMetadata metadata, Dictionary<string, string> parameters, string extra, ref FilterCriteriaSet filter)
{
	if (!string.IsNullOrEmpty(extra))
	{
	    Dictionary<string, string> dictionary = JsonConvert.DeserializeObject<Dictionary<string, string>>(HttpUtility.UrlDecode(extra));

	    if (dictionary != null && dictionary.ContainsKey("budgetitemid"))
	    {
	        FilterCriteriaSet invoiceFilter;
	        Guid biId;                    
	        if (Guid.TryParse(dictionary["budgetitemid"], out biId))
	        {
	            invoiceFilter = FilterCriteriaSet.Or.Custom(string.Format("EXISTS (SELECT * FROM InvoiceBudgetItem WHERE InvoiceId = Invoice.Id AND BudgetItemId = '{0}')", biId.ToString()));
	        }
	        else
	        {
	            invoiceFilter = FilterCriteriaSet.And.Custom("1 = 0");                        
	        }

	        filter = filter.IsEmpty ? invoiceFilter : FilterCriteriaSet.And.Merge(filter).Merge(invoiceFilter);

	        if(biId != Guid.Empty)
	        {
	            DynamicEntityRepository rep = new DynamicEntityRepository();
	            var biMetadata = rep.GetEntityMetadataByEntityName("BudgetItem");
	            var bi = rep.GetEntitiesByMetadata(biMetadata, FilterCriteriaSet.And.Equal(biId, biMetadata.GetColumn("Id"))).Entities.First();
	            Guid? sourceId = bi.SourceInvoiceId;
	            if (sourceId.HasValue)
	            {
	                var sourceInvoiceFilter = FilterCriteriaSet.Or.Equal(sourceId.ToString(), metadata.GetColumn("Id"));
	                filter = filter.IsEmpty ? sourceInvoiceFilter : FilterCriteriaSet.Or.Merge(filter).Merge(sourceInvoiceFilter);
	            }
	        };
	    }
	}            
	return ExternalMethodCallResult.SucessResult;
}

